{"routine": {"id": 1,"name": "Activate Sun Position at Sunset on Motion or Party Cam Online","parent": 0,"if": [
  {"at": {"sunset": 0}},
  {"and": 1},
  {"(": 1},
    {"IS": {"device": "n006_272637780_m", "control": "DON"}},
    {"or": 1},
    {"GE": {"device": "n006_306491825_m", "status": "ST", "value": 1, "uom": "25", "precision": 0}},
  {"")": 1}
],"then": [
  {"device": "n008_sunctrl", "command": "DON"}
],"else": [],"reasoning": "Turn on Sun Position device at sunset if motion detected at Front Door or Party Cam is online."}}

{"routine": {"id": 2,"name": "Query Party Cam and Activate Sun Position if Offline","parent": 0,"if": [
  {"at": {"time": "08:30"}}
],"then": [
  {"device": "n006_306491825_m", "command": "QUERY"}
],"else": [],"reasoning": "Query Party Cam status daily at 8:30 AM and then Sun Position device will be turned on if Party Cam is offline."}}

{"routine": {"id": 3,"name": "Turn On Sun Position if Party Cam Offline at 8:30 AM","parent": 0,"if": [
  {"at": {"time": "08:30"}},
  {"and": 1},
  {"GE": {"device": "n006_306491825_m", "status": "ST", "value": 0, "uom": "25", "precision": 0}}
],"then": [
  {"device": "n008_sunctrl", "command": "DON"}
],"else": []}}

{"routine": {"id": 4,"name": "Nightly Sun Position Off with Motion Override","parent": 0,"if": [
  {"from": {"time": "22:00", "to": {"time": "06:00", "day": 1}}}
],"then": [
  {"device": "n008_sunctrl", "command": "DOF"}
],"else": []}}

{"routine": {"id": 5,"name": "Turn On Sun Position on Front Door Motion at Night","parent": 0,"if": [
  {"from": {"time": "22:00", "to": {"time": "06:00", "day": 1}}},
  {"and": 1},
  {"IS": {"device": "n006_272637780_m", "control": "DON"}}
],"then": [
  {"device": "n008_sunctrl", "command": "DON"}
],"else": []}}
