{"routine": {"id": 1,"name": "Downstairs temp high after 6PM fan and upstairs cool set","parent": 0,"if": [
  {"GE": {"device": "n002_t521759638640", "status": "ST", "value": 780, "uom": "fahrenheit", "precision": 1}},
  {"and": 1},
  {"from": {"time": "18:00", "to": {"time": "22:00", "day": 0}}}
],"then": [
  {"device": "n002_t521759638640", "command": "Fan Mode", "parameters": [{"name": "n/a", "value": 0, "uom": "68"}]},
  {"device": "n002_t521782496039", "command": "Mode", "parameters": [{"name": "n/a", "value": 2, "uom": "67"}]},
  {"device": "n002_t521782496039", "command": "Cool Setpoint", "parameters": [{"name": "n/a", "value": 740, "uom": "fahrenheit", "precision": 1}]}
],"else": [
  {"from": {"time": "22:00", "to": {"time": "22:00", "day": 0}}},
  {"device": "n002_t521759638640", "command": "Fan Mode", "parameters": [{"name": "n/a", "value": 0, "uom": "68"}]},
  {"device": "n002_t521782496039", "command": "Mode", "parameters": [{"name": "n/a", "value": 0, "uom": "67"}]}
],"reasoning": "Activate fan auto downstairs and cool upstairs at 74F if temp above 78F after 6PM; else at 10PM set downstairs fan auto and upstairs off."}}

{"routine": {"id": 2,"name": "Network Closet occupancy weekday climate and fan control","parent": 0,"if": [
  {"IS": {"device": "n002_rs_8lln", "control": "GV1", "parameters": [{"name": "n/a", "value": 1, "uom": "25"}]}},
  {"and": 1},
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "09:00", "to": {"time": "17:00"}}}}
],"then": [
  {"device": "n002_t521759638640", "command": "Climate Type", "parameters": [{"name": "n/a", "value": 1, "uom": "25"}, {"name": "Hold Type", "value": 1, "uom": "25"}]},
  {"device": "n002_t521759638640", "command": "Fan Mode", "parameters": [{"name": "n/a", "value": 1, "uom": "68"}, {"name": "Hold Type", "value": 1, "uom": "25"}]}
],"else": [
  {"IS": {"device": "n002_rs_8lln", "control": "GV1", "parameters": [{"name": "n/a", "value": 0, "uom": "25"}]}},
  {"and": 1},
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "09:00", "to": {"time": "17:00"}}}},
  {"device": "n002_t521759638640", "command": "Climate Type", "parameters": [{"name": "n/a", "value": 0, "uom": "25"}, {"name": "Hold Type", "value": 0, "uom": "25"}]},
  {"device": "n002_t521759638640", "command": "Fan Mode", "parameters": [{"name": "n/a", "value": 0, "uom": "68"}, {"name": "Hold Type", "value": 0, "uom": "25"}]}
],"reasoning": "Adjust downstairs climate and fan mode based on network closet occupancy during weekdays 9AM-5PM."}}

{"routine": {"id": 3,"name": "Weekday heating control downstairs based on temp","parent": 0,"if": [
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "09:00", "to": {"time": "17:00"}}}},
  {"and": 1},
  {"LT": {"device": "n002_t521759638640", "status": "ST", "value": 680, "uom": "fahrenheit", "precision": 1}}
],"then": [
  {"device": "n002_t521759638640", "command": "Mode", "parameters": [{"name": "n/a", "value": 1, "uom": "67"}]},
  {"device": "n002_t521759638640", "command": "Heat Setpoint", "parameters": [{"name": "n/a", "value": 700, "uom": "fahrenheit", "precision": 1}]},
  {"device": "n002_t521759638640", "command": "Fan Mode", "parameters": [{"name": "n/a", "value": 1, "uom": "68"}, {"name": "Hold Type", "value": 2, "uom": "25"}]}
],"else": [
  {"device": "n002_t521759638640", "command": "Mode", "parameters": [{"name": "n/a", "value": 3, "uom": "67"}]},
  {"device": "n002_t521759638640", "command": "Fan Mode", "parameters": [{"name": "n/a", "value": 0, "uom": "68"}, {"name": "Hold Type", "value": 0, "uom": "25"}]}
],"reasoning": "Control downstairs heating and fan during weekdays 9AM-5PM based on temperature below 68F."}}
